package server

import (
	"net/http"
	"text/template"

	"github.com/zquestz/s/providers"
)

var (
	logo = ``
	tmpl = `<!doctype html>
<html>
  <head>
    <meta charset="UTF-8">
    <style>
* {font-family: "Tahoma", "Geneva", sans-serif;}
body {padding: 1em; background: #272F32; color: #DAEAEF;}
a, a:visited {color: #FFF;}
a:active, a:hover {color: #9DBDC6;}
input {font-size: 1.2em; padding: .2em .4em; margin-right: 5px;}
input[type=text] {width: 100%; max-width: 400px;}
select {float:right;}
form, footer {text-align: center;}
footer {display: block; padding: 1em; font-size: 1em;}
div.logo {padding: 1em;}
div.logo img { width: 100px; height: 129px;}
    </style>
    <title>s</title>
  </head>
  <body>
    <form action="/search" method="POST">
      <div>
        <select name="provider" tabindex="2">
{{range .Providers}}          <option{{.|Selected}}>{{.}}</option>
{{end}}        </select>
      </div><br />
      <div class="logo">
        <img src="data:image/png;base64,` + logo + `" alt="s" />
      </div>
      <input class="input" type="text" name="q" tabindex="1" autofocus required /><input type="submit" value="Search" tabindex="3" />
    </form>
    <footer><a href="https://github.com/zquestz/s">https://github.com/zquestz/s</a></footer>
  </body>
</html>
`
)

type templateVars struct {
	Providers       []string
	defaultProvider string
}

func index(provider string, w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/html")

	t := template.New("search")

	selected := func(p string) string {
		if p == provider {
			return " selected"
		}

		return ""
	}

	t.Funcs(template.FuncMap{
		"Selected": selected,
	})

	t, _ = t.Parse(tmpl)

	tvars := templateVars{
		Providers:       providers.ProviderNames(),
		defaultProvider: provider,
	}

	t.Execute(w, tvars)
}
